l // indent string = string // json_key ( \"properties\" , indent ) // \"{\" if ( present ( indent )) string = string // nl // repeat ( indent , 2 ) string = string // json_key ( \"totalCharge\" , indent ) // json_value ( nint ( mol % charge )) if ( mol % uhf > 0 ) then string = string // \",\" if ( present ( indent )) string = string // nl // repeat ( indent , 2 ) string = string // json_key ( \"totalSpinMultiplicity\" , indent ) // json_value ( mol % uhf + 1 ) end if if ( present ( indent )) string = string // nl // indent string = string // \"}\" if ( allocated ( mol % bond )) then string = string // \",\" if ( present ( indent )) string = string // nl // indent string = string // json_key ( \"bonds\" , indent ) // \"{\" if ( present ( indent )) string = string // nl // repeat ( indent , 2 ) string = string // json_key ( \"connections\" , indent ) // \"{\" if ( present ( indent )) string = string // nl // repeat ( indent , 3 ) string = string // json_key ( \"index\" , indent ) // json_array ([ mol % bond ( 1 : 2 , :) - 1 ], 3 , indent ) if ( present ( indent )) string = string // nl // repeat ( indent , 2 ) string = string // \"}\" if ( size ( mol % bond , 1 ) > 2 ) then string = string // \",\" if ( present ( indent )) string = string // nl // repeat ( indent , 2 ) string = string // json_key ( \"order\" , indent ) // json_array ( mol % bond ( 3 , :), 2 , indent ) if ( present ( indent )) string = string // nl // indent end if string = string // \"}\" end if if ( any ( mol % periodic )) then call dlat_to_cell ( mol % lattice , cellpar ) cellpar ( 1 : 3 ) = cellpar ( 1 : 3 ) * autoaa cellpar ( 4 : 6 ) = cellpar ( 4 : 6 ) * 180 / pi string = string // \",\" if ( present ( indent )) string = string // nl // indent string = string // json_key ( \"unitCell\" , indent ) // \"{\" if ( present ( indent )) string = string // nl // repeat ( indent , 2 ) string = string // json_key ( \"a\" , indent ) // json_value ( cellpar ( 1 ), \"(es23.16)\" ) string = string // \",\" i